import React, { useState } from "react";

interface Customer {
  customerId: number;
  name: string;
  city: string;
  contactNumber: string;
  year: number;
  photo: string;
  totalPurchasesPerYear: number;
}

const CustCrud: React.FC = () => {
    const [newCustomer, setNewCustomer] = useState<Customer>({
        customerId: 0,
        name: "",
        city: "",
        contactNumber: "",
        year: 0,
        photo: "",
        totalPurchasesPerYear: 0,
      });

    return (
        <>
          <h2>Customer Management</h2>
          <div>
            <h3>Add / Edit Customer</h3>
            <input
              type="text"
              name="name"
              placeholder="Name"
              value={newCustomer.name}
              onChange={handleInputChange}
            />
            <input
              type="text"
              name="city"
              placeholder="City"
              value={newCustomer.city}
              onChange={handleInputChange}
            />
            <input
              type="text"
              name="contactNumber"
              placeholder="Contact Number"
              value={newCustomer.contactNumber}
              onChange={handleInputChange}
            />
            <input
              type="number"
              name="year"
              placeholder="Year"
              value={newCustomer.year}
              onChange={handleInputChange}
            />
            <input
              type="text"
              name="photo"
              placeholder="Photo URL"
              value={newCustomer.photo}
              onChange={handleInputChange}
            />
            <input
              type="number"
              name="totalPurchasesPerYear"
              placeholder="Total Purchases"
              value={newCustomer.totalPurchasesPerYear}
              onChange={handleInputChange}
            />
            <button onClick={addCustomer}>Add Customer</button>
          </div>
          <div>
            <h3>Customer List</h3>
            <ul>
              {customers.map((customer) => (
                <li key={customer.customerId}>
                  <p>Name: {customer.name}</p>
                  <p>City: {customer.city}</p>
                  <p>Contact: {customer.contactNumber}</p>
                  <p>Year: {customer.year}</p>
                  <img src={customer.photo || "https://via.placeholder.com/50"} alt={customer.name} />
                  <p>Total Purchases: ${customer.totalPurchasesPerYear}</p>
                  <button>Edit</button>
                  <button>Delete</button>
                  {/* Dynamically load CustomerDetail component for each customer */}

                </li>
              ))}
            </ul>
          </div>
        </>
      );
    };


export default CustCrud;
