import React, { useState } from "react";

// Task Components
const Home = () => (
  <div>
    <h1>Welcome to the Customer Management App</h1>
    <p>Select a task to perform from the buttons below.</p>
  </div>
);

const CustomerCRUD = () => (
  <div>
    <h1>Customer Management</h1>
    <p>Perform CRUD operations on customer data here.</p>
    {/* You can implement the CRUD functionality here */}
  </div>
);

const Analytics = () => (
  <div>
    <h1>Customer Analytics</h1>
    <p>Analyze total purchases or other metrics here.</p>
  </div>
);

const Settings = () => (
  <div>
    <h1>App Settings</h1>
    <p>Modify application preferences here.</p>
  </div>
);

// Main App Component
function App() {
  const [activeComponent, setActiveComponent] = useState("Home");

  // Function to render the selected component
  const renderComponent = () => {
    switch (activeComponent) {
      case "CustomerCRUD":
        return <CustomerCRUD />;
      case "Analytics":
        return <Analytics />;
      case "Settings":
        return <Settings />;
      default:
        return <Home />;
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>CRUD Operation</h1>
        <div style={{ marginBottom: "20px" }}>
          {/* Navigation Buttons */}
          <button onClick={() => setActiveComponent("Home")} style={buttonStyle}>
            TASK 1
          </button>
          <button onClick={() => setActiveComponent("CustomerCRUD")} style={buttonStyle}>
            Customer Management
          </button>
          <button onClick={() => setActiveComponent("Analytics")} style={buttonStyle}>
            Analytics
          </button>
          <button onClick={() => setActiveComponent("Settings")} style={buttonStyle}>
            Settings
          </button>
        </div>
        {/* Render Selected Component */}
        <div style={{}}>{renderComponent()}</div>
      </header>
    </div>
  );
}

// Inline Styles for Buttons and Content
const buttonStyle = {
  padding: "10px 15px",
  margin: "5px",
  backgroundColor: "#007BFF",
  color: "#FFF",
  border: "none",
  borderRadius: "5px",
  cursor: "pointer",
};

const contentStyle = {
  padding: "20px",
  backgroundColor: "#f9f9f9",
  borderRadius: "10px",
  width: "80%",
  margin: "0 auto",
  textAlign: "left",
};

export default App;
